import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import './css/Contract.css';
import folderLogo from './assets/folder.png';
import webLogo from './assets/web_logo.png';
import documentLogo from './assets/document_logo.png';
import textLogo from './assets/text_logo.png';
import { Route, Routes } from 'react-router-dom';
import Unite from './Unite';
import ContractText from './ContractText';

const Contract = () => {
  const [activeComponent, setActiveComponent] = useState({ title: "", isOpen: false });
  const navigate = useNavigate();

  const openComponent = (title) => {
    setActiveComponent({ title, isOpen: true });
  };

  const closeComponent = () => {
    setActiveComponent({ title: "", isOpen: false });
    navigate("/computer/contract");
  };

  const headerClassName = activeComponent.isOpen ? `${activeComponent.title.toLowerCase()}-header` : '';

  return (
    <div className="contract-container">
      <div className="contract-header">
        <div className="contract-title">
          <img src={folderLogo} alt="folder" className="folder-logo"/>
          <h2>Contract</h2>
        </div>
        <div className="contract-close">
          <a href="/computer">X</a>
        </div>
      </div>
      <div className="contract-body">
        <div className="contract-item">
        <Link to="/computer/internet/website">
          <img src={webLogo} alt="web logo" className="contract-item-icon" />
        <br/>LetzChat Website Redesign
        </Link>
        </div>
        <div className="contract-item">
          <Link to="contract-text" onClick={() => openComponent('ContractText')}>
        <img src={textLogo} alt="document logo" className="contract-item-icon" />
        <br/>contract_
        <br/>projects.txt
        </Link>
        </div>
        <div className="contract-item">
          <Link to="unite" onClick={() => openComponent('Unite')}>
        <img src={documentLogo} alt="unite logo" className="contract-item-icon" />
        <br/>unite.wmv
        </Link>
        </div>
      </div>

      {activeComponent.isOpen && (
        <div className="contract-content">
          <div className={`contract-content-header ${headerClassName}`}>
            <div className="contract-content-title">
              <h2>{activeComponent.title}</h2>
            </div>
            <div className="contract-content-close">
              <button onClick={closeComponent}>X</button>
            </div>
          </div>

          <Routes>
            <Route path="/computer/contract/unite" element={<Unite />} />
            <Route path="/computer/contract/contract-text" element={<ContractText />} />
          </Routes>
        </div>
      )}
    </div>
  );
}

export default Contract;
