import React, { useRef, useState, useEffect } from 'react';

const Camera = ({}) => {
  const videoRef = useRef(null); // Ref for the video element to access the webcam
  const canvasRef = useRef(null); // Ref for the canvas element to capture photos
  const [photos, setPhotos] = useState([]); // State to store captured photo URLs
  const [stream, setStream] = useState(null); // State to store the stream from the webcam

  // Function to capture a photo from the webcam feed
  const takePhoto = () => {
    const canvas = canvasRef.current;
    const video = videoRef.current;
    if (canvas && video) {
      const context = canvas.getContext('2d');
      context.drawImage(video, 0, 0, canvas.width, canvas.height);
      const imageUrl = canvas.toDataURL('image/png'); // Converts the canvas drawing into a URL
      setPhotos([...photos, imageUrl]); // Adds the new photo URL to the photos state
    }
  };

  useEffect(() => {
    // Function to request access to the webcam
    const enableStream = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        setStream(stream);
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (err) {
        console.error(err);
      }
    };

    if (!stream) {
      enableStream();
    }

    // Cleanup function to stop the stream when the component unmounts
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, [stream]); // Dependency array includes stream so that useEffect runs again if stream changes

  return (
    <div>
      <video ref={videoRef} autoPlay playsInline width="640" height="480" />
      <button onClick={takePhoto}>Take Photo</button>
      <canvas ref={canvasRef} width="640" height="480" style={{ display: 'none' }} />
      <div className="gallery">
        {photos.map((photo, index) => (
          <img key={index} src={photo} alt={`Captured ${index}`} width="100" />
        ))}
      </div>
    </div>
  );
};

export default Camera;
