import React, { useState, useEffect } from 'react';
import axios from 'axios';

// A component to display the content of a file
const FileViewer = ({ repoFullName, filePath }) => {
  const [fileContent, setFileContent] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (filePath) {
      setLoading(true);
      // GitHub API endpoint to fetch the content of a file
      axios.get(`https://api.github.com/repos/${repoFullName}/contents/${filePath}`)
        .then(response => {
          // Assuming the file is a text file, we decode the content from base64
          const content = atob(response.data.content);
          setFileContent(content);
        })
        .catch(error => console.error(error))
        .finally(() => setLoading(false));
    }
  }, [repoFullName, filePath]);

  if (loading) return <div>Loading...</div>;

  return (
    <pre>
      <code>
        {fileContent}
      </code>
    </pre>
  );
};

export default FileViewer;
