import React, { useState, useEffect } from 'react';
import axios from 'axios';

// A component to display the content of a file
const FileViewer = ({ repoFullName, filePath }) => {
  const [fileContent, setFileContent] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (filePath) {
      setLoading(true);
      setError(null);
      // GitHub API endpoint to fetch the content of a file
      axios.get(`https://api.github.com/repos/${repoFullName}/contents/${filePath}`)
        .then(response => {
          // Check if the content is base64 encoded
          if (response.data.encoding === 'base64') {
            // If it's base64 encoded, decode it
            const content = atob(response.data.content.replace(/\n/g, ''));
            setFileContent(content);
          } else {
            // If not, set the content as is
            setFileContent(response.data.content);
          }
        })
        .catch(error => {
          console.error(error);
          setError('Failed to load file content.');
        })
        .finally(() => setLoading(false));
    }
  }, [repoFullName, filePath]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <pre>
      <code>
        {fileContent}
      </code>
    </pre>
  );
};

export default FileViewer;
