import React, { useEffect, useState } from 'react';
import axios from 'axios';
import FileViewer from './FileViewer'; // make sure to import the FileViewer component

const RepoContents = ({ repoFullName, onFileSelect }) => {
  const [contents, setContents] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    axios.get(`https://api.github.com/repos/${repoFullName}/contents/`)
      .then(response => {
        setContents(response.data); // array of objects for files and directories
        setLoading(false);
      })
      .catch(error => {
        console.error(error);
        setLoading(false);
      });
  }, [repoFullName]);

  if (loading) return <div>Loading contents...</div>;

  return (
    <div>
      <h3>Contents of {repoFullName}</h3>
      <ul>
        {contents.map((file) => (
          <li key={file.path} onClick={() => onFileSelect(file.path)}>
            {file.type === 'dir' ? 'üìÅ' : 'üìÑ'} {file.name}
          </li>
        ))}
      </ul>
    </div>
  );
};

