      })
      .catch(error => console.error(error));
  }, []);

  const handleRepoClick = (repo) => {
    setSelectedRepo(repo);
    setSelectedFilePath(''); // Reset the file path when a new repo is clicked
  };

  const handleFileClick = (filePath) => {
    setSelectedFilePath(filePath);
  };

  return (
    <div>
      <h2>GitHub Repositories</h2>
      {selectedRepo == null ? (
        <div>
          {repos.map(repo => (
            <div key={repo.id} onClick={() => handleRepoClick(repo)}>
              <h3>{repo.name}</h3>
              {/* ... other repo info ... */}
            </div>
          ))}
        </div>
      ) : selectedFilePath === '' ? (
        // If a repository is selected, but no file path is selected, show the repo contents
        <RepoContents repoFullName={selectedRepo.full_name} onFileSelect={handleFileClick} />
      ) : (
        // If a repository and a file path is selected, display the FileViewer component
        <FileViewer repoFullName={selectedRepo.full_name} filePath={selectedFilePath} />
      )}
    </div>
  );
};

export default GitHub;
