import React, { useState } from 'react';
import TextToolbar from './TextToolbar';

const ContractProjectsBody = () => {
  const [style, setStyle] = useState({
    fontWeight: 'normal',
    fontStyle: 'normal',
    textDecoration: 'none',
    textAlign: 'left',
    fontSize: '14px'
  });

  const handleStyleChange = (format) => {
    switch (format) {
      case 'bold':
        setStyle({ ...style, fontWeight: style.fontWeight === 'bold' ? 'normal' : 'bold' });
        break;
      case 'italic':
        setStyle({ ...style, fontStyle: style.fontStyle === 'italic' ? 'normal' : 'italic' });
        break;
      case 'underline':
        setStyle({ ...style, textDecoration: style.textDecoration === 'underline' ? 'none' : 'underline' });
        break;
      case 'justifyLeft':
        setStyle({ ...style, textAlign: 'left' });
        break;
      case 'justifyCenter':
        setStyle({ ...style, textAlign: 'center' });
        break;
      case 'justifyRight':
        setStyle({ ...style, textAlign: 'right' });
        break;
      default:
        break;
    }
  };

  const handleFontSizeChange = (fontSize) => {
    setStyle({ ...style, fontSize });
  };

  return (
    <div className="contract_projects-body">
      <TextToolbar onStyleChange={handleStyleChange} onFontSizeChange={handleFontSizeChange} />
      <div style={style} className='contract_projects-body-text'>
        <p>
            This folder holds the projects that I have worked on under contract, but does not include all of them.
            <br />
            <br />
            The actual projects and code I am not able to share since they are owned by the company I worked for and are paid products.
            <br />
            <br />
            However, I can share the following projects that I have worked on under contract and/or a preview of the product:
            <br />
            <br />
            1. LetzChat Website Redesign
            <br />
            2. unite.mp4
            <br />
            3. contract_projects.txt
            <br />
            <br />
            The product I am not able to show, since it is backend only and is not a visual product:
            <br />
            <br />
            Real-time phone translation app:
            <br />
            <br />
            - This app translates phone calls using speech-to-text and text-to-speech APIs.
            <br />
            - The app would both speak the translation on the call to the other caller, as well as record the conversation real-time on a transcript available for view/download.
            <br />
            - The call can hold multiple people, but the translation is only between two of 
        </p>
      </div>
    </div>
  );
};

export default ContractProjectsBody;
