import React, { useEffect, useState } from 'react';
import axios from 'axios';
import FileViewer from './FileViewer'; // make sure to import the FileViewer component

const GitHub = () => {
  const [repos, setRepos] = useState([]);
  const [selectedRepo, setSelectedRepo] = useState(null);
  const [selectedFilePath, setSelectedFilePath] = useState('');

  useEffect(() => {
    axios.get('https://api.github.com/users/d-lil/repos')
      .then(response => {
        setRepos(response.data);
      })
      .catch(error => console.error(error));
  }, []);

  const handleRepoClick = (repo) => {
    // Here you could fetch and set the contents of the repo if necessary
    setSelectedRepo(repo);
  };

  // You could add a method to select the file path when a file is clicked
  const handleFileClick = (filePath) => {
    setSelectedFilePath(filePath);
  };

  return (
    <div>
      <h2>GitHub Repositories</h2>
      {selectedRepo == null ? (
        <div>
          {repos.map(repo => (
            <div key={repo.id} onClick={() => handleRepoClick(repo)}>
              <h3>{repo.name}</h3>
              {/* ... other repo info ... */}
            </div>
          ))}
        </div>
      ) : (
        // If a repository is selected, display the FileViewer component
        <FileViewer repoFullName={selectedRepo.full_name} filePath={selectedFilePath} />
      )}
    </div>
  );
};

export default GitHub;
