// Camera.js
import React, { useRef, useEffect, useState } from 'react';
import Gallery from './Gallery';

const Camera = ({ setPhotos, onDelete, photos }) => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [stream, setStream] = useState(null);

  const takePhoto = () => {
    const canvas = canvasRef.current;
    const video = videoRef.current;
    if (canvas && video) {
      const context = canvas.getContext('2d');
      context.drawImage(video, 0, 0, canvas.width, canvas.height);
      const imageUrl = canvas.toDataURL('image/png');
      setPhotos((prevPhotos) => [...prevPhotos, imageUrl]);
    }
  };

  useEffect(() => {
    const enableStream = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        setStream(stream); // Set the stream state
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (err) {
        console.error(err);
      }
    };
  
    if (!stream) {
      enableStream();
    }
  
    // Cleanup function
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop()); // Stop each track
      }
    };
  }, [stream]);
  

  return (
    <div>
      <video ref={videoRef} autoPlay playsInline width="380" height="300" />
      <button onClick={takePhoto}>Take Photo</button>
      <canvas ref={canvasRef} width="380" height="300" style={{ display: 'none' }} />
<Gallery />
    </div>
  );
};

export default Camera;
