import React, { useRef, useState, useEffect } from 'react';

const Camera = ({ setPhotos, onDelete }) => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [stream, setStream] = useState(null);
  const [localPhotos, setLocalPhotos] = useState([]); // Local state to store photos taken during the current session

  useEffect(() => {
    const enableStream = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        setStream(stream);
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (err) {
        console.error(err);
      }
    };
  
    if (!stream) {
      enableStream();
    }
  
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, [stream]);

  const takePhoto = () => {
    const canvas = canvasRef.current;
    const video = videoRef.current;
    if (canvas && video) {
      const context = canvas.getContext('2d');
      context.drawImage(video, 0, 0, canvas.width, canvas.height);
      const imageUrl = canvas.toDataURL('image/png');
      setLocalPhotos((prevPhotos) => [...prevPhotos, imageUrl]);
      setPhotos((prevPhotos) => [...prevPhotos, imageUrl]);
    }
  };

  const handleDeleteLocalPhoto = (index) => {
    const updatedPhotos = [...localPhotos];
    updatedPhotos.splice(index, 1);
    setLocalPhotos(updatedPhotos);
    // Optionally, update the main photo state if necessary
  };

  return (
    <div>
      <video ref={videoRef} autoPlay playsInline width="380" height="300" />
      <button onClick={takePhoto}>Take Photo</button>
      <canvas ref={canvasRef} width="380" height="300" style={{ display: 'none' }} />

      {/* Display the locally taken photos */}
      <div className="local-photos-display">
        {localPhotos.map((photo, index) => (
          <div key={index}>
            <img src={photo} alt="Captured" width="100" />

          </div>
          
        ))}
                    {selectedForDeletion === index && (
              <div className="delete-confirmation">
                Delete? 
                <button onClick={() => confirmDeletion(true, index)}>Yes</button>
                <button onClick={() => confirmDeletion(false)}>No</button>
              </div>
            )}
      </div>
    </div>
  );
};

export default Camera;
