import React, { useState } from 'react';
import leftLogo from './assets/left_logo.png';
import rightLogo from './assets/right_logo.png';
import centerLogo from './assets/center_logo.png';

const TextToolbar = ({ onStyleChange, onFontSizeChange }) => {
  return (
    <div className="contract_projects-toolbar">
      <select onChange={(e) => onFontSizeChange(e.target.value)} className='font-deco'>
        <option value="12px">12px</option>
        <option value="14px">14px</option>
        <option value="16px">16px</option>
        <option value="18px">18px</option>
        <option value="20px">20px</option>
        {/* ... other font sizes ... */}
      </select>
      <button onClick={() => onStyleChange('bold')} className='font-deco'>B</button>
      <button onClick={() => onStyleChange('italic')} className='font-deco'>I</button>
      <button onClick={() => onStyleChange('underline')} className='font-deco'>U</button>
      <button onClick={() => onStyleChange('justifyLeft')} className='font-deco'><img src={leftLogo} alt="left justify icon" className='justify-icon' /></button>
      <button onClick={() => onStyleChange('justifyCenter')} className='font-deco'><img src={centerLogo} alt="center justify icon" className='justify-icon' /></button>
      <button onClick={() => onStyleChange('justifyRight')} className='font-deco'><img src={rightLogo} alt="right justify icon" className='justify-icon'/></button>
    </div>
  );
};

export default TextToolbar;
